import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FileUpload.css';
import logo from './assets/sonar-logo.png';
import { useNavigate } from 'react-router-dom';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);
    const [previousFiles, setPreviousFiles] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchPreviousFiles = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/results');
                setPreviousFiles(response.data);
            } catch (error) {
                console.error('Error fetching previous files:', error);
            }
        };
        fetchPreviousFiles();
    }, []);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) return;

        setLoading(true);
        setResult(null);

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setResult(response.data);
            setPreviousFiles([...previousFiles, response.data]);
        } catch (error) {
            console.error('Error uploading file:', error);
            setResult('An error occurred while processing the file.');
        } finally {
            setLoading(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setFile(e.dataTransfer.files[0]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const handleViewResult = (filename) => {
        navigate('/result', { state: { filename } });
    };

    return (
        <div className="file-upload-container">
            <div className="menu-bar">
                <div className="logo-container">
                    <img src={logo} alt="Sonar Logo" className="homepage-logo" />
                </div>
            </div>
            <h1>Upload your PDF</h1>
            <form onSubmit={handleSubmit}>
                <div 
                    className="dropzone"
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                    onClick={() => document.getElementById('fileInput').click()}
                >
                    <input 
                        type="file" 
                        id="fileInput" 
                        style={{ display: 'none' }} 
                        onChange={handleFileChange} 
                    />
                    {file ? <p>{file.name}</p> : <p>Drag and drop a file here or click to select a file</p>}
                </div>
                <button type="submit">Upload</button>
            </form>
            {loading && <div className="loading-screen">
                <img src={logo} alt="Sonar Logo" className="loading-logo" />
                <p className="loading-text">Sonar is analyzing your file</p>
            </div>}
            {result && (
                <div className="result">
                    <h2>Table of Contents: {result["TOC Pages"]}</h2>
                    <pre>{JSON.stringify(result["Sections"], null, 2)}</pre>
                </div>
            )}
            <h2>Previously Uploaded</h2>
            <div className="previous-files">
                {previousFiles.map((file, index) => (
                    <div key={index} className="previous-file" onClick={() => handleViewResult(file.filename)}>
                        {file.filename}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FileUpload;
