import React, { useState } from 'react';
import axios from 'axios';
import './FileUpload.css';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) return;

        setLoading(true);
        setResult(null);

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            setResult(response.data);
        } catch (error) {
            console.error('Error uploading file:', error);
            setResult('An error occurred while processing the file.');
        } finally {
            setLoading(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setFile(e.dataTransfer.files[0]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    return (
        <div className="file-upload-container">
            <h1>Segment PDF</h1>
            <form onSubmit={handleSubmit}>
                <div 
                    className="dropzone"
                    onDrop={handleDrop}
                    onDragOver={handleDragOver}
                    onClick={() => document.getElementById('fileInput').click()}
                >
                    <input 
                        type="file" 
                        id="fileInput" 
                        style={{ display: 'none' }} 
                        onChange={handleFileChange} 
                    />
                    {file ? <p>{file.name}</p> : <p>Drag and drop a file here or click to select a file</p>}
                </div>
                <button type="submit">Upload</button>
            </form>
            {loading && <div className="loading">File indexing...</div>}
            {result && (
                <div className="result">
                    <h2>Table of Contents: {result["TOC Pages"]}</h2>
                    <pre>{JSON.stringify(result["Sections"], null, 2)}</pre>
                </div>
            )}
        </div>
    );
};

export default FileUpload;
