import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResultPage.css';
import logo from './assets/sonar-logo.png';  // Ensure the path is correct
import { useLocation, useNavigate } from 'react-router-dom';

const ResultPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [result, setResult] = useState(null);
    const file = location.state?.file;
    const filename = location.state?.filename;

    useEffect(() => {
        const fetchResult = async () => {
            try {
                const response = await axios.get(`http://127.0.0.1:5000/results/${filename}`);
                setResult(response.data);
            } catch (error) {
                console.error('Error fetching result:', error);
            } finally {
                setLoading(false);
            }
        };

        if (file) {
            const uploadFile = async () => {
                if (!file) return;

                const formData = new FormData();
                formData.append('file', file);

                try {
                    const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                        headers: {
                            'Content-Type': 'multipart/form-data'
                        }
                    });
                    setResult(response.data);
                } catch (error) {
                    console.error('Error uploading file:', error);
                    setResult('An error occurred while processing the file.');
                } finally {
                    setLoading(false);
                }
            };

            uploadFile();
        } else if (filename) {
            fetchResult();
        }
    }, [file, filename]);

    const handleGoBack = () => {
        navigate('/');
    };

    const formatResult = (data) => {
        let formattedResult = [];
        if (data["TOC Pages"]) {
            formattedResult.push(
                <tr key="toc">
                    <td>Table of Contents</td>
                    <td>{data["TOC Pages"].replace(/Page\s+/g, '')}</td>
                </tr>
            );
        }
        if (data["Sections"]) {
            formattedResult = formattedResult.concat(
                Object.entries(data["Sections"]).map(([key, value]) => (
                    <tr key={key}>
                        <td>{key}</td>
                        <td>{value.replace(/Page\s+/g, '')}</td>
                    </tr>
                ))
            );
        }
        return formattedResult;
    };

    return (
        <div className="result-page">
            <div className="menu-bar">
                <div className="logo-container" onClick={handleGoBack}>
                    <img src={logo} alt="Sonar Logo" className="homepage-logo" />
                </div>
            </div>
            {loading ? (
                <div className="loading-screen">
                    <img src={logo} alt="Sonar Logo" className="loading-logo" />
                    <p className="loading-text">Sonar is analyzing your file</p>
                </div>
            ) : (
                <div className="result-container fade-in">
                    <h2 className="result-title">{result?.filename}</h2>
                    <table className="result-table">
                        <thead>
                            <tr>
                                <th>Section Name</th>
                                <th>Pages</th>
                            </tr>
                        </thead>
                        <tbody>
                            {result && formatResult(result)}
                        </tbody>
                    </table>
                    <button className="back-button" onClick={handleGoBack}>Back</button>
                </div>
            )}
        </div>
    );
};

export default ResultPage;
