import React, { useState, useEffect } from 'react';
import './FileUpload.css';
import logo from './assets/sonar-logo.png';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) return;

        setLoading(true);

        try {
            const response = await axios.get(`http://127.0.0.1:5000/results/${file.name}.json`);
            navigate('/result', { state: { result: response.data, filename: file.name } });
        } catch (error) {
            const formData = new FormData();
            formData.append('file', file);

            try {
                const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
                navigate('/result', { state: { result: response.data, filename: file.name } });
            } catch (error) {
                console.error('Error uploading file:', error);
            } finally {
                setLoading(false);
            }
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setFile(e.dataTransfer.files[0]);
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    return (
        <div className="file-upload-page">
            <div className="file-upload-menu-bar">
                <div className="logo-container">
                    <img src={logo} alt="Sonar Logo" className="logo" />
                </div>
            </div>
            <div className="file-upload-container">
                <h1>Upload your PDF</h1>
                <form onSubmit={handleSubmit}>
                    <div 
                        className="dropzone"
                        onDrop={handleDrop}
                        onDragOver={handleDragOver}
                        onClick={() => document.getElementById('fileInput').click()}
                    >
                        <input 
                            type="file" 
                            id="fileInput" 
                            style={{ display: 'none' }} 
                            onChange={handleFileChange} 
                        />
                        {file ? <p>{file.name}</p> : <p>Drag and drop a file here or click to select a file</p>}
                    </div>
                    <div className="upload-button-container">
                        <button type="submit" className="upload-button">Upload</button>
                    </div>
                </form>
                {loading && (
                    <div className="loading-screen">
                        <img src={logo} alt="Sonar Logo" className="loading-logo" />
                        <p className="loading-text">Sonar is analyzing your file</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default FileUpload;
