import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResultPage.css';
import logo from './assets/sonar-logo.png';
import { useLocation, useNavigate } from 'react-router-dom';

const ResultPage = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [loading, setLoading] = useState(true);
    const [result, setResult] = useState(null);
    const [query, setQuery] = useState('');
    const file = location.state?.file;

    useEffect(() => {
        const uploadFile = async () => {
            if (!file) return;

            const formData = new FormData();
            formData.append('file', file);
            formData.append('query', query);

            try {
                const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data'
                    }
                });
                setResult(response.data);
            } catch (error) {
                console.error('Error uploading file:', error);
                setResult('An error occurred while processing the file.');
            } finally {
                setLoading(false);
            }
        };

        uploadFile();
    }, [file, query]);

    const handleGoBack = () => {
        navigate('/');
    };

    const handleQueryChange = (event) => {
        setQuery(event.target.value);
    };

    const formatResult = (data) => {
        let formattedResult = [];
        if (data["TOC Pages"]) {
            formattedResult.push(
                <tr key="toc">
                    <td>Table of Contents</td>
                    <td>{data["TOC Pages"].replace(/Page\s+/g, '')}</td>
                </tr>
            );
        }
        if (data["Sections"]) {
            formattedResult = formattedResult.concat(
                Object.entries(data["Sections"]).map(([key, value]) => (
                    <tr key={key}>
                        <td>{key}</td>
                        <td>{value.replace(/Page\s+/g, '')}</td>
                    </tr>
                ))
            );
        }
        if (data["Relevant Sections"]) {
            formattedResult = formattedResult.concat(
                data["Relevant Sections"].map(({ section, score, pages }) => (
                    <tr key={section}>
                        <td>{section} (Score: {score.toFixed(2)})</td>
                        <td>{`Page ${pages[0]} - ${pages[1]}`}</td>
                    </tr>
                ))
            );
        }
        return formattedResult;
    };

    return (
        <div className="result-page">
            {loading ? (
                <div className="loading-screen">
                    <img src={logo} alt="Sonar Logo" className="loading-logo" />
                    <p className="loading-text">is analyzing your file</p>
                </div>
            ) : (
                <div className="result-container fade-in">
                    <h2 className="result-title">{file?.name}</h2>
                    <input
                        type="text"
                        value={query}
                        onChange={handleQueryChange}
                        placeholder="Enter your sector/industry"
                        className="query-input"
                    />
                    <table className="result-table">
                        <thead>
                            <tr>
                                <th>Section Name</th>
                                <th>Pages</th>
                            </tr>
                        </thead>
                        <tbody>
                            {result && formatResult(result)}
                        </tbody>
                    </table>
                    <button className="back-button" onClick={handleGoBack}>Back</button>
                </div>
            )}
        </div>
    );
};

export default ResultPage;
